import React, { useRef } from 'react';
import Modal from 'react-bootstrap/Modal';

function MyVerticallyCenteredModal(props) {

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_dwi8um6', 'template_vh2lnie', form.current, '35jxlMRE2-DZ_dIhv')
      .then((result) => {
          console.log(result.text);
          navigate("/Thankyou")
      }, (error) => {
          console.log(error.text);
      });
  };
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      
      <div class="modal-body snipcss-ZybQN style-dMsXU" id="style-dMsXU">
  <div class="left_col style-oSMoE" id="style-oSMoE">
    <img src="https://i.imgur.com/JcONViR.jpg" alt="" class="img-fluid"/>
  </div>
  <div class="right_col">
    <div class="animated_form">
      <div class="form-head">
        <h3 class="form_heading style-BjAUn" id="style-BjAUn">
        Get in Touch
        </h3>
        <h4 class="form_heading">
          Send us a message
        </h4>
      </div>
      <div class="form-group">
        <input class="form-input" name="name" type="text" placeholder="Your Name" required="" id="qSenderNamemodalpopup"/>
      </div>
    
      <div className="form-group" >
        <input class="form-input numberText number-only" type="number" name="Mobile No." placeholder="Mobile Number" required=""/>
      </div>
     
      <input type="submit" value="Request A Call" class="btn form-control" id="SubmitQuerymodalpopup"/>
    </div>
  </div>
</div>
      
    </Modal>
  );
}

export default function Dialog() {
  const [modalShow, setModalShow] = React.useState(false);

  return (
    <>
      <div class='btn-effect-1' onClick={() => setModalShow(true)}>
        Consult Now
      </div>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}
